// 3 agent'ın response'larını topla ve parse et
const items = $input.all();

// Her agent'ın response'unu güvenli şekilde parse et
function parseAgentResponse(item) {
  const input = item.json;
  let content = '';

  // Input bir object ise (simplifyOutput: true olduğunda)
  if (input.message && input.message.content) {
    content = input.message.content;
  }
  // Input bir array ise
  else if (Array.isArray(input) && input.length > 0) {
    if (input[0].message && input[0].message.content) {
      content = input[0].message.content;
    } else if (input[0].content) {
      content = input[0].content;
    } else {
      content = JSON.stringify(input[0]);
    }
  }
  // Diğer formatlar
  else if (input.content) {
    content = input.content;
  } else {
    content = JSON.stringify(input);
  }

  // JSON parse et (eğer string ise)
  try {
    if (typeof content === 'string') {
      return JSON.parse(content);
    }
    return content;
  } catch (e) {
    // Parse edilemezse, basit bir object oluştur
    return {
      decision: 'needs_review',
      reasoning: content,
      confidence: 0.5
    };
  }
}

// Agent response'larını parse et
const planning = parseAgentResponse(items[0]);
const production = parseAgentResponse(items[1]);
const warehouse = parseAgentResponse(items[2]);

// Agent bilgilerini topla
const agents = [
  { name: 'Planning', decision: planning.decision || 'needs_review', reasoning: planning.reasoning || '', confidence: planning.confidence || 0.5 },
  { name: 'Production', decision: production.decision || 'needs_review', reasoning: production.reasoning || '', confidence: production.confidence || 0.5 },
  { name: 'Warehouse', decision: warehouse.decision || 'needs_review', reasoning: warehouse.reasoning || '', confidence: warehouse.confidence || 0.5 }
];

// Konsensüs analizi
const approveCount = agents.filter(a => a.decision === 'approved').length;
const rejectCount = agents.filter(a => a.decision === 'rejected').length;
const needsReviewCount = agents.filter(a => a.decision === 'needs_review').length;

// Consensus prompt oluştur
const consensusPrompt = `3 agent'tan gelen cevaplar:\n\n${agents.map((a, i) => 
  `${i+1}. ${a.name} Agent:\n   Karar: ${a.decision}\n   Güven: ${a.confidence}\n   Gerekçe: ${a.reasoning}\n`
).join('\n')}\n\nKonsensüs: ${approveCount} approve, ${rejectCount} reject, ${needsReviewCount} needs_review\n\nBu 3 agent'ın cevaplarını analiz et ve nihai karar ver.`;

return {
  json: {
    agents,
    consensus: { 
      approve: approveCount, 
      reject: rejectCount,
      needs_review: needsReviewCount
    },
    consensusPrompt
  }
};

// Manager agent'ın response'unu parse et
const managerInput = $input.item.json;
const aggregateData = $('Aggregate Responses').item.json;

// Manager response'unu güvenli şekilde parse et
let managerContent = '';

// Input bir object ise (simplifyOutput: true olduğunda)
if (managerInput.message && managerInput.message.content) {
  managerContent = managerInput.message.content;
}
// Input bir array ise
else if (Array.isArray(managerInput) && managerInput.length > 0) {
  if (managerInput[0].message && managerInput[0].message.content) {
    managerContent = managerInput[0].message.content;
  } else if (managerInput[0].content) {
    managerContent = managerInput[0].content;
  } else {
    managerContent = JSON.stringify(managerInput[0]);
  }
}
// Diğer formatlar
else if (managerInput.content) {
  managerContent = managerInput.content;
} else {
  managerContent = JSON.stringify(managerInput);
}

// JSON parse et
let managerDecision;
try {
  if (typeof managerContent === 'string') {
    managerDecision = JSON.parse(managerContent);
  } else {
    managerDecision = managerContent;
  }
} catch (e) {
  managerDecision = {
    finalDecision: 'needs_review',
    reasoning: managerContent,
    confidence: 0.5,
    consensus: aggregateData.consensus
  };
}

// Final response oluştur
const response = {
  success: true,
  workflow: 'multi-agent-consensus',
  finalDecision: managerDecision.finalDecision || 'needs_review',
  consensus: managerDecision.consensus || aggregateData.consensus,
  agentResponses: aggregateData.agents,
  managerReasoning: managerDecision.reasoning || '',
  confidence: managerDecision.confidence || 0.5
};

return {
  json: response
};
