const agentOutput = $input.item.json;

// Agent node output formatı: [{"output": "..."}]
let content = '';

if (Array.isArray(agentOutput) && agentOutput.length > 0) {
  if (agentOutput[0].output && typeof agentOutput[0].output === 'string') {
    content = agentOutput[0].output;
  } else if (agentOutput[0].text) {
    content = agentOutput[0].text;
  } else {
    content = JSON.stringify(agentOutput[0]);
  }
} else if (agentOutput.output && typeof agentOutput.output === 'string') {
  content = agentOutput.output;
} else if (agentOutput.text) {
  content = agentOutput.text;
} else if (typeof agentOutput === 'string') {
  content = agentOutput;
} else {
  content = JSON.stringify(agentOutput);
}

// JSON parse etmeyi dene
let parsed = null;
try {
  parsed = typeof content === 'string' ? JSON.parse(content) : content;
} catch (e) {
  // JSON parse edilemezse, text içinde JSON aramayı dene
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    try {
      parsed = JSON.parse(jsonMatch[0]);
    } catch (e2) {
      parsed = null;
    }
  }
}

// Eğer parsed yoksa veya decision yoksa, text'ten decision çıkar
if (!parsed || !parsed.decision) {
  const lowerContent = content.toLowerCase();
  let decision = 'needs_review';
  let confidence = 0.5;

  // Decision kelimelerini kontrol et (öncelik sırasına göre)
  if (lowerContent.includes('rejected') || lowerContent.includes('reject') || lowerContent.includes('cannot') || lowerContent.includes('unable') || lowerContent.includes('not possible')) {
    decision = 'rejected';
    confidence = 0.8;
  } else if (lowerContent.includes('approved') || lowerContent.includes('approve') || lowerContent.includes('can proceed') || lowerContent.includes('ready') || lowerContent.includes('sufficient') || lowerContent.includes('initiated')) {
    decision = 'approved';
    confidence = 0.8;
  } else if (lowerContent.includes('needs_review') || lowerContent.includes('needs review') || lowerContent.includes('review') || lowerContent.includes('clarification') || lowerContent.includes('further')) {
    decision = 'needs_review';
    confidence = 0.6;
  }

  parsed = parsed || {};
  parsed.decision = decision;
  parsed.reasoning = parsed.reasoning || content.substring(0, 500);
  parsed.confidence = parsed.confidence || confidence;
}

// Validate ve normalize
const result = {
  decision: parsed.decision && ['approved', 'rejected', 'needs_review'].includes(parsed.decision.toLowerCase()) ? parsed.decision.toLowerCase() : 'needs_review',
  reasoning: parsed.reasoning && typeof parsed.reasoning === 'string' ? parsed.reasoning : (parsed.reasoning || content.substring(0, 500) || 'Gerekçe belirtilmedi'),
  confidence: typeof parsed.confidence === 'number' && parsed.confidence >= 0 && parsed.confidence <= 1 ? parsed.confidence : 0.5
};

return {
  json: result
};

