// Parse node'larından gelen parse edilmiş output'ları al
const inputs = $input.all();

// Node adına göre eşleştirme yap
let planning = null;
let production = null;
let warehouse = null;

// Her input'u kontrol et ve node adına göre eşleştir
for (const item of inputs) {
  const json = item.json || {};
  const nodeName = item._node?.name || '';

  // Node adına göre eşleştir
  if (nodeName.includes('Planning') || (!planning && json.decision)) {
    planning = json;
  } else if (nodeName.includes('Production') || (!production && json.decision && planning)) {
    production = json;
  } else if (nodeName.includes('Warehouse') || (!warehouse && json.decision && planning && production)) {
    warehouse = json;
  }
}

// Eğer hala eşleşmeyen varsa, sırayla ata
if (!planning && inputs.length > 0) planning = inputs[0]?.json || null;
if (!production && inputs.length > 1) production = inputs[1]?.json || null;
if (!warehouse && inputs.length > 2) warehouse = inputs[2]?.json || null;

// Default değerler
planning = planning || { decision: 'needs_review', reasoning: 'Planning agent henüz çalışmadı', confidence: 0.5 };
production = production || { decision: 'needs_review', reasoning: 'Production agent henüz çalışmadı', confidence: 0.5 };
warehouse = warehouse || { decision: 'needs_review', reasoning: 'Warehouse agent henüz çalışmadı', confidence: 0.5 };

const agents = [
  { name: 'Planning', decision: planning.decision || 'needs_review', reasoning: planning.reasoning || '', confidence: planning.confidence || 0.5 },
  { name: 'Production', decision: production.decision || 'needs_review', reasoning: production.reasoning || '', confidence: production.confidence || 0.5 },
  { name: 'Warehouse', decision: warehouse.decision || 'needs_review', reasoning: warehouse.reasoning || '', confidence: warehouse.confidence || 0.5 }
];

const approveCount = agents.filter(a => a.decision === 'approved').length;
const rejectCount = agents.filter(a => a.decision === 'rejected').length;
const needsReviewCount = agents.filter(a => a.decision === 'needs_review').length;

const consensusPrompt = `3 agent'tan gelen cevaplar:\n\n${agents.map((a, i) => `${i+1}. ${a.name} Agent:\n   Karar: ${a.decision}\n   Güven: ${a.confidence}\n   Gerekçe: ${a.reasoning}\n`).join('\n')}\n\nKonsensüs: ${approveCount} approve, ${rejectCount} reject, ${needsReviewCount} needs_review\n\nBu 3 agent'ın cevaplarını analiz et ve nihai karar ver.`;

// Webhook'tan plan_id'yi al
const webhookData = $node["Webhook Trigger"]?.json || {};
const planId = webhookData.body?.plan_id || null;

return {
  json: {
    agents,
    consensus: {
      approve: approveCount,
      reject: rejectCount,
      needs_review: needsReviewCount
    },
    consensusPrompt,
    planId
  }
};

