    try {
      parsed = JSON.parse(jsonMatch[0]);
    } catch (e2) {
      // JSON bulunamazsa, default değerler döndür
      parsed = {
        finalDecision: 'needs_review',
        reasoning: content.substring(0, 500),
        consensus: aggregate.consensus || { approve: 0, reject: 0, needs_review: 0 },
        confidence: 0.5
      };
    }
  } else {
    // JSON bulunamazsa, default değerler döndür
    parsed = {
      finalDecision: 'needs_review',
      reasoning: content.substring(0, 500),
      consensus: aggregate.consensus || { approve: 0, reject: 0, needs_review: 0 },
      confidence: 0.5
    };
  }
}

// JSON Schema validation ve normalize
const result = {
  finalDecision: parsed.finalDecision && ['approved', 'rejected', 'needs_review'].includes(parsed.finalDecision) ? parsed.finalDecision : 'needs_review',
  reasoning: parsed.reasoning && typeof parsed.reasoning === 'string' ? parsed.reasoning : (parsed.reasoning || content.substring(0, 500) || 'Gerekçe belirtilmedi'),
  consensus: parsed.consensus && typeof parsed.consensus === 'object' ? {
    approve: typeof parsed.consensus.approve === 'number' ? parsed.consensus.approve : (aggregate.consensus?.approve || 0),
    reject: typeof parsed.consensus.reject === 'number' ? parsed.consensus.reject : (aggregate.consensus?.reject || 0),
    needs_review: typeof parsed.consensus.needs_review === 'number' ? parsed.consensus.needs_review : (aggregate.consensus?.needs_review || 0)
  } : (aggregate.consensus || { approve: 0, reject: 0, needs_review: 0 }),
  confidence: typeof parsed.confidence === 'number' && parsed.confidence >= 0 && parsed.confidence <= 1 ? parsed.confidence : 0.5
};

return {
  json: result
};

