// Tüm input'ları al (3 agent'tan gelen cevaplar)
const inputs = $input.all();

// Debug: Input sayısını ve içeriğini kontrol et
// console.log('Total inputs:', inputs.length);
// inputs.forEach((item, idx) => console.log(`Input ${idx}:`, Object.keys(item.json || {})));

// Agent'ları sırayla eşleştir (Planning, Production, Warehouse)
let planning = null;
let production = null;
let warehouse = null;

// Her input'u kontrol et - n8n'de input'lar genellikle sırayla gelir
// İlk input Planning, ikinci Production, üçüncü Warehouse olmalı
if (inputs.length > 0) planning = inputs[0].json;
if (inputs.length > 1) production = inputs[1].json;
if (inputs.length > 2) warehouse = inputs[2].json;

// Alternatif: Output içeriğine göre eşleştirme
for (const item of inputs) {
  const json = item.json || {};

  // Output içeriğini kontrol et
  let outputContent = '';
  if (Array.isArray(json) && json.length > 0) {
    outputContent = json[0].output || json[0].text || JSON.stringify(json[0]);
  } else if (json.output) {
    outputContent = json.output;
  } else if (json.text) {
    outputContent = json.text;
  } else {
    outputContent = JSON.stringify(json);
  }

  // Output içeriğine göre agent'ı belirle
  const lowerContent = outputContent.toLowerCase();

  // Planning Agent genellikle "planning", "capacity" gibi kelimeler içerir
  if (!planning && (lowerContent.includes('planning') || lowerContent.includes('capacity') || lowerContent.includes('plan'))) {
    planning = json;
  }
  // Production Agent genellikle "production", "operator" gibi kelimeler içerir
  else if (!production && (lowerContent.includes('production') || lowerContent.includes('operator') || lowerContent.includes('manufacturing'))) {
    production = json;
  }
  // Warehouse Agent genellikle "warehouse", "stock", "material" gibi kelimeler içerir
  else if (!warehouse && (lowerContent.includes('warehouse') || lowerContent.includes('stock') || lowerContent.includes('material') || lowerContent.includes('inventory'))) {
    warehouse = json;
  }
  // Eğer hiçbiri eşleşmezse, sırayla ata
  else if (!planning) {
    planning = json;
  } else if (!production) {
    production = json;
  } else if (!warehouse) {
    warehouse = json;
  }
}

function parseAgent(input, name) {
  if (!input) {
    return {
      decision: 'needs_review',
      reasoning: name + ' agent henüz çalışmadı veya cevap vermedi',
      confidence: 0.5
    };
  }

  let content = '';

  // n8n Agent node output formatı: [{"output": "..."}]
  if (Array.isArray(input) && input.length > 0) {
    if (input[0].output && typeof input[0].output === 'string') {
      content = input[0].output;
    } else if (input[0].text) {
      content = input[0].text;
    } else {
      content = JSON.stringify(input[0]);
    }
  }
  // Object formatı
  else if (input.output && typeof input.output === 'string') {
    content = input.output;
  } else if (input.text) {
    content = input.text;
  } else if (typeof input === 'string') {
    content = input;
  } else {
    content = JSON.stringify(input);
  }

  // JSON parse etmeyi dene
  try {
    if (typeof content === 'string') {
      const parsed = JSON.parse(content);
      if (parsed && typeof parsed === 'object' && parsed.decision) {
        return parsed;
      }
      if (typeof parsed === 'string') {
        return JSON.parse(parsed);
      }
      return parsed;
    }
    return content;
  } catch (e) {
    // JSON parse edilemezse, text içinde JSON aramayı dene
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      try {
        return JSON.parse(jsonMatch[0]);
      } catch (e2) {
        // JSON bulunamazsa, text'i decision olarak kullan
        const lowerContent = content.toLowerCase();
        let decision = 'needs_review';
        if (lowerContent.includes('rejected') || lowerContent.includes('reject')) {
          decision = 'rejected';
        } else if (lowerContent.includes('approved') || lowerContent.includes('approve')) {
          decision = 'approved';
        }
        return {
          decision: decision,
          reasoning: content.substring(0, 500),
          confidence: decision !== 'needs_review' ? 0.8 : 0.5
        };
      }
    }
    // JSON bulunamazsa, text'i decision olarak kullan
    const lowerContent = content.toLowerCase();
    let decision = 'needs_review';
    if (lowerContent.includes('rejected') || lowerContent.includes('reject')) {
      decision = 'rejected';
    } else if (lowerContent.includes('approved') || lowerContent.includes('approve')) {
      decision = 'approved';
    }
    return {
      decision: decision,
      reasoning: content.substring(0, 500),
      confidence: decision !== 'needs_review' ? 0.8 : 0.5
    };
  }
}

const p = parseAgent(planning, 'Planning');
const pr = parseAgent(production, 'Production');
const w = parseAgent(warehouse, 'Warehouse');

const agents = [
  { name: 'Planning', decision: p.decision || 'needs_review', reasoning: p.reasoning || '', confidence: p.confidence || 0.5 },
  { name: 'Production', decision: pr.decision || 'needs_review', reasoning: pr.reasoning || '', confidence: pr.confidence || 0.5 },
  { name: 'Warehouse', decision: w.decision || 'needs_review', reasoning: w.reasoning || '', confidence: w.confidence || 0.5 }
];

const approveCount = agents.filter(a => a.decision === 'approved').length;
const rejectCount = agents.filter(a => a.decision === 'rejected').length;
const needsReviewCount = agents.filter(a => a.decision === 'needs_review').length;

const consensusPrompt = `3 agent'tan gelen cevaplar:\n\n${agents.map((a, i) => `${i+1}. ${a.name} Agent:\n   Karar: ${a.decision}\n   Güven: ${a.confidence}\n   Gerekçe: ${a.reasoning}\n`).join('\n')}\n\nKonsensüs: ${approveCount} approve, ${rejectCount} reject, ${needsReviewCount} needs_review\n\nBu 3 agent'ın cevaplarını analiz et ve nihai karar ver.`;

// Webhook'tan plan_id'yi al
const webhookData = $node["Webhook Trigger"]?.json || {};
const planId = webhookData.body?.plan_id || null;

return {
  json: {
    agents,
    consensus: {
      approve: approveCount,
      reject: rejectCount,
      needs_review: needsReviewCount
    },
    consensusPrompt,
    planId
  }
};

const managerRaw = $input.item.json;
const aggregate = $node["Aggregate Responses"].json;

let content = '';
if (managerRaw.output && typeof managerRaw.output === 'string') {
  content = managerRaw.output;
} else if (managerRaw.text) {
  content = managerRaw.text;
} else if (typeof managerRaw === 'string') {
  content = managerRaw;
} else {
  content = JSON.stringify(managerRaw);
}

let parsed;
try {
  parsed = typeof content === 'string' ? JSON.parse(content) : content;
} catch (e) {
  parsed = {
    finalDecision: 'needs_review',
    reasoning: 'Manager Agent cevabı parse edilemedi.',
    confidence: 0.5
  };
}

return {
  json: {
    success: true,
    workflow: 'multi-agent-consensus-supabase-tool',
    finalDecision: parsed.finalDecision || 'needs_review',
    consensus: parsed.consensus || aggregate.consensus,
    agentResponses: aggregate.agents,
    managerReasoning: parsed.reasoning || 'Gerekçe belirtilmedi.',
    confidence: parsed.confidence || 0.5,
    planId: aggregate.planId
  }
};
