// OpenAI node'dan gelen response'u al
const input = $input.item.json;

// Response formatını kontrol et ve content'i çıkar
let content = '';

// Input bir object ise (simplifyOutput: true olduğunda)
if (input.message && input.message.content) {
  content = input.message.content;
}
// Input bir array ise
else if (Array.isArray(input) && input.length > 0) {
  if (input[0].message && input[0].message.content) {
    content = input[0].message.content;
  } else if (input[0].content) {
    content = input[0].content;
  } else {
    content = JSON.stringify(input[0]);
  }
}
// Diğer formatlar
else if (input.content) {
  content = input.content;
} else {
  // Fallback: tüm input'u string'e çevir
  content = JSON.stringify(input);
}

// Response oluştur
const response = {
  success: true,
  agent: "planning",
  response: content
};

return {
  json: response
};
